<li <?php if(empty($_GET['context']) || $_GET['context'] !== 'edit') echo get_block_wrapper_attributes( array('class' => "mr-5 text-xs shrink-0", ) ); else echo 'data-wp-block-props="true"'; ?>>
    <a class="block hover:text-slate-200" href="<?php echo (!empty($_GET['context']) && $_GET['context'] === 'edit') ? 'javascript:void()' : PG_Blocks_v2::getLinkUrl( $args, 'social_link' ) ?>" target="_blank" rel="noreferrer noopener" title="<?php echo PG_Blocks_v2::getAttribute( $args, 'link_title' ) ?>" aria-label="<?php echo PG_Blocks_v2::getAttribute( $args, 'link_title' ) ?>"><span class="sr-only"><?php echo PG_Blocks_v2::getAttribute( $args, 'link_title' ) ?></span><?php if ( !PG_Blocks_v2::getImageUrl( $args, 'social_icon', 'full', false ) && PG_Blocks_v2::getImageSVG( $args, 'social_icon' ) ) : ?><?php echo PG_Blocks_v2::mergeInlineSVGAttributes( PG_Blocks_v2::getImageSVG( $args, 'social_icon' ), array( 'class' => 'h-6 w-6', 'aria-hidden' => 'true' ) ) ?><?php endif; ?><?php if ( PG_Blocks_v2::getImageUrl( $args, 'social_icon', 'full', false ) ) : ?><img aria-hidden="true" src="<?php echo PG_Blocks_v2::getImageUrl( $args, 'social_icon', 'full' ); ?>" class="h-6 w-6 <?php echo (PG_Blocks_v2::getImageField( $args, 'social_icon', 'id', true) ? ('wp-image-' . PG_Blocks_v2::getImageField( $args, 'social_icon', 'id', true)) : '') ?>"/><?php endif; ?></a>
</li>